import React from 'react';
import './App.css';
import Meals from './sections/meals/meals.js';
import Orders from './sections/orders/orders.js';
import { fetchMeals } from './services/fetchMeals.js';
import { fetchOrder } from './services/fetchOrder';

class App extends React.Component {
  state = {
    meals: [], orders: '', mealSelect: ''
  }

  getMeals = async () => {
    const res = await fetchMeals();
    this.setState({ meals: res.data });
  }

  postOrder = async (body) => {
    const res = await fetchOrder(body);
  }

  componentDidMount() {
    this.getMeals();
  }

  ableButton = (e) => {
    const meals = Array.from(document.getElementsByClassName('selected'));
    console.log(this.state.mealSelect);
    if (this.state.mealSelect !== '') {
      console.log(meals);
      meals.map((x) => {

        x.classList.remove('selected');
      });
    }
    this.setState({ mealSelect: String(e.target.attributes.meal_id.textContent) });
    e.target.classList.add('selected');
    const button = document.getElementById('submit');
    button.removeAttribute('disabled');

  }

  checkOrder = (event) => {
    e.preventDefault();
    this.postOrder({ meal_id: this.state.mealSelect, user_id: 'Chanchito feliz' })
  }

  render() {
    return (
      <div className="App" >
        <Meals meals={this.state.meals} selectMeal={this.ableButton} mealSelect={this.state.mealSelect} checkOrder={this.checkOrder} />
        <Orders orders={this.state.orders} />
      </div>
    );
  }
}


export default App;
