import React from 'react';
import './App.css';
import Meals from './sections/meals/meals.js';
import Orders from './sections/orders/orders.js';
import { fetchMeals } from './services/fetchMeals.js';

class App extends React.Component {
  state = {
    meals: [], orders: '', mealSelect: null
  }

  getMeals = async () => {
    const res = await fetchMeals();
    this.setState({ meals: res.data });
  }

  componentDidMount() {
    this.getMeals();
  }

  ableButton = (e) => {
    this.setState({mealSelect: ''});
    console.log(e.target.attribute);
    const button = document.getElementById('submit');
    button.removeAttribute('disabled');

  }

  render() {
    return (
      <div className="App" >
        <Meals meals={this.state.meals} selectMeal={this.ableButton} />
        <Orders orders={this.state.orders} />
      </div>
    );
  }
}


export default App;
